import React from "react";
import axios from "axios";
//import ImageUploader from "react-images-upload";

export default class AddItem extends React.Component {
  state = {
    meal_name: "",
    price: 0,
    //image: null,
    special_meal: "",
    quantity: 0,
  };

  handleChange = (event) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const item_list = {
      meal_name: this.state.meal_name,
      price: this.state.price,
      special_meal: this.state.special_meal,
      quantity: this.state.quantity,
    };
    console.log(item_list);
    axios.post(`http://0.0.0.0:8000/Items/`, item_list).then((res) => {
      console.log(res);
      console.log(res.data);
    });
  };

  /*
  handleSubmit = event => {
    event.preventDefault ();
    const item_list = {
      meal_name: this.state.meal_name,
      price: this.state.price,
      special_meal: this.state.special_meal,
      quantity: this.state.quantity,
    };
    fetch ('http://0.0.0.0:8000/Items/', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
      },
      body: JSON.stringify (item_list),
    }).then (response => response.json ());
    window.location.reload ();
  };
*/
  render() {
    return (
      <form onSubmit={this.handleSubmit.bind(this)}>
        <label>
          <input
            type="text"
            placeholder="Meal"
            name="meal_name"
            onChange={this.handleChange}
          />
          <input
            type="number"
            placeholder="Price"
            name="price"
            onChange={this.handleChange}
          />
          {/*<ImageUploader
            withIcon={true}
            buttonText="Choose images"
            onChange={this.handleChange}
            imgExtension={[".jpg", ".gif", ".png", ".gif"]}
            maxFileSize={5242880}
          />
          */}
          <input
            type="text"
            placeholder="Special meal"
            name="special_meal"
            onChange={this.handleChange}
          />
          <input
            type="number"
            placeholder="Quantity"
            name="quantity"
            onChange={this.handleChange}
          />
        </label>
        <button type="submit">ADD ITEM</button>
      </form>
    );
  }
}
